package com.damanjit.tflliveroadstatus/** * Data class to store JSON converted response from the TfL server in one place */data class DataClassRoadStatus(    val displayName: String="",    val statusSeverity: String="",    val statusSeverityDescription: String="",    // In case of error the following will be populated    val httpStatusCode:Int=Int.MIN_VALUE,    val message:String="")